// services/api/dashboard.js
import api from '../api';

const BASE_URL = '/api/dashboard';

export const dashboardAPI = {
    // Statistici generale
    getStats: async () => {
        const response = await api.get(`${BASE_URL}/stats`);
        return response.data;
    },

    // Documente care expiră
    getExpiringDocuments: async () => {
        const response = await api.get(`${BASE_URL}/documents/expiring`);
        return response.data;
    },

    // Status șoferi
    getDriverStatus: async () => {
        const response = await api.get(`${BASE_URL}/drivers/status`);
        return response.data;
    }
};

export default dashboardAPI;
